import express from "express";
import blog from "../controllers/blog";
import asyncHandler from "../middlewares/async";
import { protect } from "../middlewares/auth";

const router = express.Router();

router
  .route("/")
  .post(asyncHandler(blog.create))
  .get(asyncHandler(blog.getAll));
router
  .route("/:id")
  .get(asyncHandler(blog.getOne))
  .put(asyncHandler(protect, blog.update))
  .delete(asyncHandler(protect, blog.delete));

router.route("/:id/comments").post(asyncHandler(blog.comment));

export default router;

import uploader from "../config/cloudinary";
import Post from "../modal/blog";
import Comment from "../modal/comment";
import errorResponse from "../util/errorResponse";

class blog {
  static async create(req, res, next) {
    const post = await Post.create({
      ...req.body,
      imageUrl: "",
      imageId: "",
      time: Date.now(),
    });

    if (req.files) {
      const tmp = req.files.image.tempFilePath;
      const result = await uploader.upload(tmp, (_, result) => result);
      post.imageUrl = result.url;
      post.imageId = result.public_id;
      post.save();

      return res.status(201).json({ success: true, data: post });
    }
  }

  static async getAll(req, res, next) {
    const post = await Post.find();
    res.status(201).json({ success: true, count: post.length, data: post });
  }

  static async getOne(req, res, next) {
    const post = await Post.findById(req.params.id).populate("comments");
    if (!post) {
      return next(
        new errorResponse(`Post not found with id of ${req.params.id}`, 404)
      );
    }

    return res.status(200).json({ success: true, data: post });
  }

  static async update(req, res, next) {
    const post = await Post.findByIdAndUpdate(req.params.id, req.body, {
      new: true,
      runValidators: true,
    });
    if (!post) {
      return next(
        new errorResponse(`Post not found with id of ${req.params.id}`, 404)
      );
    }

    return res.status(200).json({ success: true, data: post });
  }

  static async delete(req, res, next) {
    const post = await Post.findByIdAndDelete(req.params.id);
    if (!post) {
      return next(
        new errorResponse(`Post not found with id of ${req.params.id}`, 404)
      );
    }
    return res
      .status(200)
      .json({ success: true, msg: "successfully delete post" });
  }

  static async comment(req, res, next) {
    const { name, message } = req.body;
    const comment = await Comment.create({
      name,
      message,
    });

    const post = await Post.findById(req.params.id);
    post.comments.push(comment.id);
    post.commentCounts += 1;
    await post.save();

    return res.status(201).json({
      success: true,
      msg: "comment successfully created",
      data: post,
    });
  }
}

export default blog;

